import React from 'react';
import { useNavigate } from 'react-router-dom';
import Button from './Button';
import '../card.css';

const SearchBox = ({ onSearch }) => {
  const navigate = useNavigate();

  const handleSearch = (e) => {
    e.preventDefault();
    const query = e.target.elements.search.value;
    onSearch(query);
  };

  const handleCuriosity = () => {
    window.location.href = 'https://entretenimento.uol.com.br/noticias/redacao/2019/05/29/game-of-thrones-os-dramas-da-vida-real-que-o-elenco-da-serie-enfrentou.htm';
  };

  const handleExtra = () => {
    navigate('/extra');
  };

  const buttonHeight = "40px";

  return (
    <form className="search-bar" onSubmit={handleSearch} style={{ display: 'flex', alignItems: 'center' }}>
      <input type="text" name="search" className="search-input" placeholder="Pesquisar..." style={{ flex: '1', height: buttonHeight, marginRight: '10px', padding: '0 10px', border: '1px solid #ccc', borderRadius: '5px' }} />
      <Button type="submit" label="Pesquisa" className="search-button" style={{ height: buttonHeight }} />
      <Button label="Curiosidade" className="button" onClick={handleCuriosity} style={{ height: buttonHeight, marginLeft: '10px' }} />
      <Button label="Extra" className="button" onClick={handleExtra} style={{ height: buttonHeight, marginLeft: '10px' }} />
    </form>
  );
};

export default SearchBox;
