import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { fetchCharacters } from '../api/api'; // Corrigido o caminho do arquivo api.js
import CardList from './components/CardList';
import SearchBox from './components/SearchBox';
import ExtraImages from './components/ExtraImages'; // Importação do componente ExtraImages
import './card.css';

const App = () => {
  const [page, setPage] = useState(1);
  const [searchQuery, setSearchQuery] = useState('');
  const [characters, setCharacters] = useState([]);

  useEffect(() => {
    const fetchCharactersData = async () => {
      try {
        const data = await fetchCharacters(page);
        setCharacters(data);
      } catch (error) {
        console.error('Error fetching characters:', error);
      }
    };

    fetchCharactersData();
  }, [page, searchQuery]);

  const handleNextPage = () => {
    setPage(page + 1);
  };

  const handlePreviousPage = () => {
    if (page > 1) {
      setPage(page - 1);
    }
  };

  const handleSearch = (query) => {
    setSearchQuery(query);
  };

  return (
    <div className="app">
      <div className="title">
        <h1>Game of Thrones</h1>
      </div>
      <SearchBox onSearch={handleSearch} />
      <Routes>
        <Route path="/" element={<CardList characters={characters} />} />
        <Route path="/extra" element={<ExtraImages />} /> {/* Rota para o componente ExtraImages */}
      </Routes>
      <div className="pagination">
        {page > 1 && (
          <button onClick={handlePreviousPage} className="button">Página Anterior</button>
        )}
        <button onClick={handleNextPage} className="button">Próxima Página</button>
      </div>
      <Link to="/" className="button">Voltar</Link>
    </div>
  );
};

export default App;
